import React, { useState, useEffect } from 'react';
import './styles.css';
import Captcha from './captcha';

const Portal = () => {
  const [aadhaarNumber, setAadhaarNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [interactionData, setInteractionData] = useState({});
  const [clickCount, setClickCount] = useState(0);
  const [keystrokeDurations, setKeystrokeDurations] = useState([]);
  const [scrollDistance, setScrollDistance] = useState(0);
  const [idleTime, setIdleTime] = useState(0);
  const sessionStart = new Date().getTime();
  let lastInteractionTime = sessionStart;
  let lastCursorPosition = { x: null, y: null, time: null };
  let lastKeyPressTime = null;

  useEffect(() => {
    const trackMouseMovement = (event) => {
      const currentTime = new Date().getTime();
      if (lastCursorPosition.x !== null) {
        const dx = event.clientX - lastCursorPosition.x;
        const dy = event.clientY - lastCursorPosition.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const timeElapsed = (currentTime - lastCursorPosition.time) / 1000; // in seconds

        if (timeElapsed > 0 && timeElapsed < 5) { // Limit to realistic time intervals
          const speed = distance / timeElapsed;
          const acceleration = distance / (timeElapsed * timeElapsed);
          setInteractionData((prevData) => ({
            ...prevData,
            avgCursorSpeed: (prevData.avgCursorSpeed * clickCount + speed) / (clickCount + 1), // Smooth out speed
            cursorAcceleration: (prevData.cursorAcceleration * clickCount + acceleration) / (clickCount + 1),
          }));
        }
      }
      lastCursorPosition = { x: event.clientX, y: event.clientY, time: currentTime };
      lastInteractionTime = currentTime;
    };

    const handleClick = () => {
      setClickCount((prev) => prev + 1);
      lastInteractionTime = new Date().getTime();
    };

    const handleKeyPress = (event) => {
      const currentTime = new Date().getTime();
      if (lastKeyPressTime) {
        const duration = currentTime - lastKeyPressTime;
        setKeystrokeDurations((prevDurations) => [...prevDurations, duration]);
      }
      lastKeyPressTime = currentTime;
      lastInteractionTime = currentTime;
    };

    const handleScroll = () => {
      const newScrollPosition = window.scrollY;
      setScrollDistance((prev) => prev + Math.abs(newScrollPosition - lastCursorPosition.y));
      lastInteractionTime = new Date().getTime();
    };

    const calculateIdleTime = () => {
      const currentTime = new Date().getTime();
      if (currentTime - lastInteractionTime > 1000) {
        setIdleTime((prev) => prev + (currentTime - lastInteractionTime) / 1000); // in seconds
      }
      lastInteractionTime = currentTime;
    };

    document.addEventListener('mousemove', trackMouseMovement);
    document.addEventListener('click', handleClick);
    document.addEventListener('keydown', handleKeyPress);
    window.addEventListener('scroll', handleScroll);

    const idleInterval = setInterval(calculateIdleTime, 1000);

    const handleBeforeUnload = () => {
      const currentTime = new Date().getTime();
      const sessionDuration = (currentTime - sessionStart) / 1000; // in seconds

      const totalKeyPressDuration = keystrokeDurations.reduce((sum, dur) => sum + dur, 0);
      const averageKeyPressDuration = keystrokeDurations.length > 0
        ? totalKeyPressDuration / keystrokeDurations.length
        : 0;

      const keyTransitionTime = keystrokeDurations.length > 1
        ? keystrokeDurations.slice(1).reduce((sum, duration, i) => sum + (keystrokeDurations[i + 1] - keystrokeDurations[i]), 0) / (keystrokeDurations.length - 1)
        : 0;

      const typingSpeed = sessionDuration > 0
        ? (keystrokeDurations.length / (sessionDuration / 60)).toFixed(2)
        : 0; // chars per minute

      const formattedData = {
        avgCursorSpeed: interactionData.avgCursorSpeed ? interactionData.avgCursorSpeed.toFixed(2) : 0,
        cursorAcceleration: interactionData.cursorAcceleration ? interactionData.cursorAcceleration.toFixed(2) : 0,
        clickPattern: clickCount,
        typingSpeed,
        keyPressDuration: averageKeyPressDuration.toFixed(2),
        keyTransitionTime: keyTransitionTime.toFixed(2),
        keyHoldTime: averageKeyPressDuration.toFixed(2),
        sessionDuration: sessionDuration.toFixed(2),
        idleTime: idleTime.toFixed(2),
        scrollBehavior: scrollDistance.toFixed(2),
      };

      downloadCSV(formattedData);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      document.removeEventListener('mousemove', trackMouseMovement);
      document.removeEventListener('click', handleClick);
      document.removeEventListener('keydown', handleKeyPress);
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('beforeunload', handleBeforeUnload);
      clearInterval(idleInterval);
    };
  }, [clickCount, keystrokeDurations, scrollDistance, idleTime, sessionStart, interactionData]);

  const downloadCSV = (data) => {
    const headers = Object.keys(data).join(',');
    const values = Object.values(data).join(',');

    const csvContent = `${headers}\n${values}`;
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.download = 'interaction_data.csv';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="portal-container">
      <h2>Login Portal</h2>
      <Captcha />
      <footer>
        <p>Note: This page collects data for research purposes.</p>
      </footer>
    </div>
  );
};

export default Portal;
