import React, { useState, useEffect } from 'react';
import './styles.css';
import Captcha from './captcha';

const Portal = () => {
  const [aadhaarNumber, setAadhaarNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [interactionData, setInteractionData] = useState({});
  const [clickCount, setClickCount] = useState(0);
  const [keystrokeDurations, setKeystrokeDurations] = useState([]);
  const [scrollDistance, setScrollDistance] = useState(0);
  const [idleTime, setIdleTime] = useState(0);
  const [dwellTimes, setDwellTimes] = useState([]);
  const sessionStart = new Date().getTime();
  let lastInteractionTime = sessionStart;
  let lastCursorPosition = { x: null, y: null, time: null };
  let lastKeyPressTime = sessionStart;

  useEffect(() => {
    const trackMouseMovement = (event) => {
      const currentTime = new Date().getTime();
      if (lastCursorPosition.x !== null) {
        const dx = event.clientX - lastCursorPosition.x;
        const dy = event.clientY - lastCursorPosition.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const timeElapsed = (currentTime - lastCursorPosition.time) / 1000; // in seconds

        if (timeElapsed > 0) {
          setInteractionData((prevData) => ({
            ...prevData,
            avgCursorSpeed: prevData.avgCursorSpeed
              ? (prevData.avgCursorSpeed + (distance / timeElapsed)) / 2
              : distance / timeElapsed,
            cursorAcceleration: prevData.cursorAcceleration
              ? (prevData.cursorAcceleration + distance / (timeElapsed * timeElapsed)) / 2
              : distance / (timeElapsed * timeElapsed),
          }));
        }
      }
      lastCursorPosition = { x: event.clientX, y: event.clientY, time: currentTime };
      lastInteractionTime = currentTime;
    };

    const handleClick = () => {
      setClickCount((prev) => prev + 1);
      lastInteractionTime = new Date().getTime();
    };

    const handleKeyPress = (event) => {
      const currentTime = new Date().getTime();
      if (keystrokeDurations.length > 0) {
        const interval = currentTime - lastKeyPressTime;
        setKeystrokeDurations((prevDurations) => [
          ...prevDurations,
          { key: event.key, time: currentTime, interval },
        ]);
      }
      lastKeyPressTime = currentTime;
      lastInteractionTime = currentTime;
    };

    const handleScroll = () => {
      const newScrollPosition = window.scrollY;
      setScrollDistance((prev) => prev + Math.abs(newScrollPosition - lastCursorPosition.y));
      lastInteractionTime = new Date().getTime();
    };

    const calculateIdleTime = () => {
      const currentTime = new Date().getTime();
      setIdleTime((prev) => prev + (currentTime - lastInteractionTime) / 1000); // in seconds
      lastInteractionTime = currentTime;
    };

    document.addEventListener('mousemove', trackMouseMovement);
    document.addEventListener('click', handleClick);
    document.addEventListener('keydown', handleKeyPress);
    window.addEventListener('scroll', handleScroll);

    const idleInterval = setInterval(calculateIdleTime, 1000);

    const handleBeforeUnload = () => {
      const sessionData = {
        avgCursorSpeed: interactionData.avgCursorSpeed || 0,
        cursorAcceleration: interactionData.cursorAcceleration || 0,
        pathDeviation: 0, // Placeholder for actual calculation logic
        idleTime,
        jitter: 0, // Placeholder for actual calculation logic
        clickPattern: clickCount,
        typingSpeed: keystrokeDurations.length / ((new Date().getTime() - sessionStart) / 60000), // chars per minute
        keyPressDuration: keystrokeDurations.length > 0
          ? keystrokeDurations.reduce((acc, val) => acc + val.interval, 0) / keystrokeDurations.length
          : 0,
        keyTransitionTime: keystrokeDurations.length > 1
          ? keystrokeDurations.reduce((acc, _, i, arr) =>
              i > 0 ? acc + (arr[i].time - arr[i - 1].time) : acc, 0
            ) / (keystrokeDurations.length - 1)
          : 0,
        keyHoldTime: keystrokeDurations.length > 0
          ? keystrokeDurations.reduce((acc, val) => acc + val.interval, 0) / keystrokeDurations.length
          : 0,
        errorRate: 0, // Placeholder for actual calculation logic
        sessionDuration: (new Date().getTime() - sessionStart) / 1000, // in seconds
        pageNavigationPattern: dwellTimes,
        averageDwellTime: dwellTimes.length > 0
          ? dwellTimes.reduce((sum, time) => sum + time, 0) / dwellTimes.length
          : 0,
        scrollBehavior: scrollDistance,
      };

      downloadCSV(sessionData);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      document.removeEventListener('mousemove', trackMouseMovement);
      document.removeEventListener('click', handleClick);
      document.removeEventListener('keydown', handleKeyPress);
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('beforeunload', handleBeforeUnload);
      clearInterval(idleInterval);
    };
  }, [interactionData, clickCount, keystrokeDurations, scrollDistance, idleTime, sessionStart, dwellTimes]);

  const downloadCSV = (data) => {
    const headers = Object.keys(data).join(',');
    const values = Object.values(data).map((value) =>
      Array.isArray(value) ? JSON.stringify(value) : value
    ).join(',');
  
    const csvContent = `data:text/csv;charset=utf-8,${headers}\n${values}`;
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.download = 'interaction_data.csv';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const handleAadhaarChange = (e) => {
    const value = e.target.value;
    if (/^\d{0,12}$/.test(value)) {
      setAadhaarNumber(value);
      setErrorMessage('');
    } else {
      setErrorMessage('Aadhaar number must be exactly 12 digits.');
    }
  };

  const handleLogin = () => {
    if (aadhaarNumber.length === 12) {
      alert('Login successful!');
    } else {
      setErrorMessage('Please enter a valid 12-digit Aadhaar number.');
    }
  };

  return (
    <div className="portal-container">
      <h2>Login Portal</h2>
      <div className="form-group">
        <label htmlFor="aadhaarNumber">Aadhaar Number:</label>
        <input
          type="text"
          id="aadhaarNumber"
          value={aadhaarNumber}
          onChange={handleAadhaarChange}
          placeholder="Enter your 12-digit Aadhaar number"
        />
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </div>
      <Captcha />
      <button id="loginButton" onClick={handleLogin} disabled={aadhaarNumber.length !== 12}>
        Login
      </button>
      <footer>
        <p>Note: This page collects data for research purposes.</p>
      </footer>
    </div>
  );
};

export default Portal;
