import React, { useState, useEffect } from 'react';
import './styles.css';
import Captcha from './captcha'; // Ensure the correct case for the file name

const Portal = () => {
  const [aadhaarNumber, setAadhaarNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [interactionLog, setInteractionLog] = useState([]);
  const [focusTimestamps, setFocusTimestamps] = useState({ focusStart: null, focusEnd: null });
  const [keystrokeIntervals, setKeystrokeIntervals] = useState([]);
  let lastKeyTime = null;

  useEffect(() => {
    const handleFocus = () => {
      setFocusTimestamps(prev => ({ ...prev, focusStart: new Date().getTime() }));
    };

    const handleBlur = () => {
      setFocusTimestamps(prev => ({
        ...prev,
        focusEnd: new Date().getTime(),
      }));
    };

    document.getElementById('aadhaarNumber').addEventListener('focus', handleFocus);
    document.getElementById('aadhaarNumber').addEventListener('blur', handleBlur);

    const handleBeforeUnload = () => {
      const sessionData = generateSessionData();
      downloadCSV(sessionData);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      document.getElementById('aadhaarNumber').removeEventListener('focus', handleFocus);
      document.getElementById('aadhaarNumber').removeEventListener('blur', handleBlur);
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handleAadhaarChange = (e) => {
    const value = e.target.value;
    if (/^\d{0,12}$/.test(value)) {
      setAadhaarNumber(value);
      setErrorMessage('');

      const currentKeyTime = new Date().getTime();
      if (lastKeyTime) {
        const interval = currentKeyTime - lastKeyTime;
        setKeystrokeIntervals(prev => [...prev, interval]);
      }
      lastKeyTime = currentKeyTime;

      setInteractionLog(prev => [
        ...prev,
        { event: 'input', timestamp: currentKeyTime, valueLength: value.length }
      ]);
    } else {
      setErrorMessage('Aadhaar number must be exactly 12 digits.');
    }
  };

  const generateSessionData = () => {
    return {
      interactionLog,
      keystrokeIntervals,
      focusDuration:
        focusTimestamps.focusEnd && focusTimestamps.focusStart
          ? (focusTimestamps.focusEnd - focusTimestamps.focusStart) / 1000 // in seconds
          : null,
    };
  };

  const downloadCSV = (data) => {
    const csvHeaders = Object.keys(data).join(',');
    const csvRows = Object.values(data)
      .map(value => (Array.isArray(value) ? JSON.stringify(value) : value))
      .join(',');

    const csvContent = `data:text/csv;charset=utf-8,${csvHeaders}\n${csvRows}`;
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);

    const link = document.createElement('a');
    link.href = url;
    link.download = 'interaction_data.csv';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const handleLogin = () => {
    if (aadhaarNumber.length === 12) {
      alert('Login successful!');
    } else {
      setErrorMessage('Please enter a valid 12-digit Aadhaar number.');
    }
  };

  return (
    <div className="portal-container">
      <h2>Login Portal</h2>
      <div className="form-group">
        <label htmlFor="aadhaarNumber">Aadhaar Number:</label>
        <input
          type="text"
          id="aadhaarNumber"
          value={aadhaarNumber}
          onChange={handleAadhaarChange}
          placeholder="Enter your 12-digit Aadhaar number"
        />
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      </div>
      <Captcha />
      <button id="loginButton" onClick={handleLogin} disabled={aadhaarNumber.length !== 12}>
        Login
      </button>
    </div>
  );
};

export default Portal;
