// frontend/src/components/CombinedLogin.jsx

import React, { useRef, useEffect, useState } from 'react';
import { gsap } from 'gsap';
import { ChevronLeft, ChevronRight, Lock, User } from 'lucide-react';
import { motion } from 'framer-motion';
import axios from 'axios';

// Import images
import flagImage from '../images/flag2.jpg';
import student from '../images/students.jpg';
import farmer from '../images/farmer.jpg';
import working from '../images/working2.jpg';

// Constants for normalization and threshold adjustments
const AVERAGE_DPI = 1600; // Adjust based on common high-DPI mouse settings
const DPI_NORMALIZATION_FACTOR = AVERAGE_DPI / 800; // Normalize for high-DPI devices
const MAX_HUMAN_ACCELERATION = 50000; // Threshold for human-like acceleration

const CombinedLogin = () => {
  // Refs for UI elements
  const formRef = useRef(null);
  const carouselRef = useRef(null);

  // State variables for UI
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isChecked, setIsChecked] = useState(false);

  // State variables for interaction data
  const [aadhaarNumber, setAadhaarNumber] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [interactionData, setInteractionData] = useState({
    avgCursorSpeed: 0,
    cursorAcceleration: 0,
    pathDeviation: 0,
    idleTime: 0,
    jitter: 0,
    clickPattern: 0,
    typingSpeed: 0,
    keyPressDuration: 0,
    keyTransitionTime: 0,
    keyTransitionStdDev: 0,
    typingAccuracy: 100,
    errorRate: 0,
    sessionDuration: 0,
    averageDwellTime: 0,
    scrollBehavior: 0,
    interactionComplexity: 0,
  });

  // Additional state variables
  const [clickCount, setClickCount] = useState(0);
  const [keystrokeDurations, setKeystrokeDurations] = useState([]);
  const [scrollDistance, setScrollDistance] = useState(0);
  const [idleTime, setIdleTime] = useState(0);
  const [errorCount, setErrorCount] = useState(0);
  const [dwellTimes, setDwellTimes] = useState([]);
  const [keyTransitionTimes, setKeyTransitionTimes] = useState([]);

  // Refs for tracking events
  const keyDownTimes = useRef({});
  const lastKeyDownTimeRef = useRef(null);
  const sessionStartRef = useRef(performance.now());
  const lastInteractionTimeRef = useRef(sessionStartRef.current);
  const lastCursorPositionRef = useRef({ x: null, y: null, time: null });
  const lastScrollPositionRef = useRef(window.scrollY);
  const lastFocusTimeRef = useRef(null);

  // Ref to keep track of the latest avgCursorSpeed
  const avgCursorSpeedRef = useRef(interactionData.avgCursorSpeed);

  // Update the ref whenever avgCursorSpeed changes
  useEffect(() => {
    avgCursorSpeedRef.current = interactionData.avgCursorSpeed;
  }, [interactionData.avgCursorSpeed]);

  // Device Detection
  const [isTouchDevice, setIsTouchDevice] = useState(false);

  useEffect(() => {
    const checkTouchDevice = () => {
      const hasTouchPoints =
        'maxTouchPoints' in navigator && navigator.maxTouchPoints > 0;
      const hasTouchEvents =
        'ontouchstart' in window || navigator.msMaxTouchPoints > 0;

      if (hasTouchPoints || hasTouchEvents) {
        setIsTouchDevice(true);
      } else {
        setIsTouchDevice(false);
      }
    };

    checkTouchDevice();

    // Optional: Re-check on window resize or other events if device capabilities can change dynamically
    window.addEventListener('resize', checkTouchDevice);

    return () => {
      window.removeEventListener('resize', checkTouchDevice);
    };
  }, []);

  // Utility function to calculate standard deviation
  const calculateStandardDeviation = (values) => {
    const n = values.length;
    if (n === 0) return 0;
    const mean = values.reduce((a, b) => a + b, 0) / n;
    const variance = values.reduce((a, b) => a + (b - mean) ** 2, 0) / n;
    return Math.sqrt(variance);
  };

  // Function to export data to CSV
  const exportToCSV = (data) => {
    try {
      console.log('exportToCSV called with data:', data);

      if (!data || typeof data !== 'object') {
        console.error('Invalid data provided to exportToCSV:', data);
        return;
      }

      const headers = Object.keys(data);
      if (headers.length === 0) {
        console.error('No headers found in data:', data);
        return;
      }

      const csvRows = [];

      // Add the headers row
      csvRows.push(headers.join(','));

      // Add the values row
      const values = headers.map(header => {
        let value = data[header];
        if (value === null || value === undefined) {
          value = '';
        } else {
          value = value.toString();
        }
        const escaped = value.replace(/"/g, '""'); // Escape double quotes by doubling them
        return `"${escaped}"`;
      });
      csvRows.push(values.join(','));

      // Combine all rows into a single CSV string
      const csvString = csvRows.join('\n');
      console.log('Generated CSV string:', csvString);

      // Check if CSV string is not empty
      if (!csvString.trim()) {
        console.error('CSV string is empty.');
        return;
      }

      // Create a Blob from the CSV string
      const blob = new Blob([csvString], { type: 'text/csv;charset=utf-8;' });
      console.log('Blob created:', blob);

      // Create a link to download the Blob
      const url = URL.createObjectURL(blob);
      console.log('Blob URL:', url);
      const link = document.createElement('a');
      link.setAttribute('href', url);
      link.setAttribute('download', 'interaction_data.csv');
      link.style.visibility = 'hidden';

      // Append the link to the DOM and trigger a click to download
      document.body.appendChild(link);
      link.click();

      // Clean up by removing the link and revoking the object URL
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      console.log('CSV export initiated successfully.');
    } catch (error) {
      console.error('Error in exportToCSV:', error);
    }
  };

  // Interaction Data Collection
  useEffect(() => {
    let mouseMoveEvents = [];

    // Throttle function to limit the frequency of event handler execution
    const throttle = (func, limit) => {
      let lastFunc;
      let lastRan;
      return function (...args) {
        const context = this;
        if (!lastRan) {
          func.apply(context, args);
          lastRan = Date.now();
        } else {
          clearTimeout(lastFunc);
          lastFunc = setTimeout(function () {
            if (Date.now() - lastRan >= limit) {
              func.apply(context, args);
              lastRan = Date.now();
            }
          }, limit - (Date.now() - lastRan));
        }
      };
    };

    const trackMouseMovement = throttle((event) => {
      const currentTime = performance.now();
      const lastCursorPosition = lastCursorPositionRef.current;

      if (lastCursorPosition.x !== null) {
        const dx =
          (event.clientX - lastCursorPosition.x) / DPI_NORMALIZATION_FACTOR;
        const dy =
          (event.clientY - lastCursorPosition.y) / DPI_NORMALIZATION_FACTOR;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const timeElapsed =
          (currentTime - lastCursorPosition.time) / 1000; // in seconds

        if (timeElapsed > 0 && timeElapsed < 5) {
          let speed = distance / timeElapsed;
          let acceleration =
            (speed - avgCursorSpeedRef.current) / timeElapsed;

          // Cap acceleration at a human threshold
          if (acceleration > MAX_HUMAN_ACCELERATION) {
            acceleration = MAX_HUMAN_ACCELERATION;
          } else if (acceleration < -MAX_HUMAN_ACCELERATION) {
            acceleration = -MAX_HUMAN_ACCELERATION;
          }

          // Collect mouse move events for advanced analysis
          mouseMoveEvents.push({
            speed,
            acceleration,
            time: currentTime,
          });

          // Keep only the last 50 events for moving average
          const recentMouseEvents = mouseMoveEvents.slice(-50);

          const avgSpeed =
            recentMouseEvents.reduce((sum, e) => sum + e.speed, 0) /
            recentMouseEvents.length;
          const avgAcceleration =
            recentMouseEvents.reduce((sum, e) => sum + e.acceleration, 0) /
            recentMouseEvents.length;

          // Calculate straight-line distance between last position and current position
          const straightLineDistance = Math.sqrt(
            (event.clientX - lastCursorPosition.x) ** 2 +
              (event.clientY - lastCursorPosition.y) ** 2
          );

          // Calculate path deviation as the absolute difference between actual distance and straight-line distance
          const deviation = Math.abs(distance - straightLineDistance);

          // Dynamic jitter threshold based on avgCursorSpeed
          const jitterThreshold = avgCursorSpeedRef.current * 0.05; // 5% of avg speed
          const movement = Math.sqrt(dx * dx + dy * dy);
          const isJitter = movement < jitterThreshold;

          setInteractionData((prevData) => {
            const updatedData = {
              ...prevData,
              avgCursorSpeed: avgSpeed,
              cursorAcceleration: avgAcceleration,
              pathDeviation:
                prevData.pathDeviation > 0
                  ? (prevData.pathDeviation * (recentMouseEvents.length - 1) +
                      deviation) /
                    recentMouseEvents.length
                  : deviation,
              jitter: isJitter ? prevData.jitter + 1 : prevData.jitter,
            };

            return updatedData;
          });
        }
      }

      lastCursorPositionRef.current = {
        x: event.clientX,
        y: event.clientY,
        time: currentTime,
      };
      lastInteractionTimeRef.current = currentTime;
    }, 100); // Throttle to once every 100ms

    const handleClick = () => {
      setClickCount((prev) => prev + 1);
      lastInteractionTimeRef.current = performance.now();
    };

    const handleScroll = () => {
      const newScrollPosition = window.scrollY;
      const delta = Math.abs(newScrollPosition - lastScrollPositionRef.current);

      if (delta > 0) {
        setScrollDistance((prev) => prev + delta);
        lastInteractionTimeRef.current = performance.now();
      }
      lastScrollPositionRef.current = newScrollPosition;
    };

    const calculateIdleTime = () => {
      const currentTime = performance.now();
      const timeSinceLastInteraction =
        (currentTime - lastInteractionTimeRef.current) / 1000; // in seconds
      if (timeSinceLastInteraction > 1) {
        setIdleTime((prev) => prev + timeSinceLastInteraction);
        lastInteractionTimeRef.current = currentTime; // Reset lastInteractionTime
      }
    };

    // Key event handlers
    const handleKeyDown = (event) => {
      const currentTime = performance.now();

      if (!keyDownTimes.current[event.code]) {
        keyDownTimes.current[event.code] = currentTime;

        // Calculate key transition time
        if (lastKeyDownTimeRef.current !== null) {
          const transitionTime = currentTime - lastKeyDownTimeRef.current;
          setKeyTransitionTimes((prevTimes) => [...prevTimes, transitionTime]);
        }
        lastKeyDownTimeRef.current = currentTime;
        lastInteractionTimeRef.current = currentTime;
      }
    };

    const handleKeyUp = (event) => {
      const currentTime = performance.now();

      if (keyDownTimes.current[event.code]) {
        const keyPressDuration = currentTime - keyDownTimes.current[event.code];
        // Record keystroke duration (key press duration)
        setKeystrokeDurations((prevDurations) => [
          ...prevDurations,
          keyPressDuration,
        ]);

        // Increment error count for specific keys
        if (event.key === 'Backspace' || event.key === 'Delete') {
          setErrorCount((prev) => prev + 1);
        }

        delete keyDownTimes.current[event.code];
      }
    };

    const handleFocusIn = (event) => {
      const target = event.target;
      if (
        target.tagName === 'INPUT' ||
        target.tagName === 'TEXTAREA' ||
        target.isContentEditable
      ) {
        target.addEventListener('keydown', handleKeyDown);
        target.addEventListener('keyup', handleKeyUp);
        lastFocusTimeRef.current = performance.now();
      }
    };

    const handleFocusOut = (event) => {
      const target = event.target;
      if (lastFocusTimeRef.current) {
        const dwellTime = performance.now() - lastFocusTimeRef.current;
        setDwellTimes((prevTimes) => [...prevTimes, dwellTime]);
        lastFocusTimeRef.current = null;
      }
      if (
        target.tagName === 'INPUT' ||
        target.tagName === 'TEXTAREA' ||
        target.isContentEditable
      ) {
        target.removeEventListener('keydown', handleKeyDown);
        target.removeEventListener('keyup', handleKeyUp);
      }
    };

    // Event listeners
    document.addEventListener('mousemove', trackMouseMovement);
    document.addEventListener('click', handleClick);
    window.addEventListener('scroll', handleScroll);
    document.addEventListener('focusin', handleFocusIn);
    document.addEventListener('focusout', handleFocusOut);

    // Idle time calculation interval
    const idleInterval = setInterval(calculateIdleTime, 5000); // Every 5 seconds

    // Cleanup on unmount
    return () => {
      document.removeEventListener('mousemove', trackMouseMovement);
      document.removeEventListener('click', handleClick);
      window.removeEventListener('scroll', handleScroll);
      document.removeEventListener('focusin', handleFocusIn);
      document.removeEventListener('focusout', handleFocusOut);
      clearInterval(idleInterval);
    };
  }, []); // Dependency array is empty as refs handle dynamic data

  // Aadhaar number input handler with validation
  const handleAadhaarChange = (e) => {
    const value = e.target.value.replace(/\s+/g, '');
    if (/^\d{0,12}$/.test(value)) {
      setAadhaarNumber(value);
      setErrorMessage('');
    } else {
      setErrorMessage('Aadhaar number must be exactly 12 digits.');
    }
  };

  // Helper function to calculate interaction complexity
  const calculateInteractionComplexity = () => {
    const {
      avgCursorSpeed,
      cursorAcceleration,
      pathDeviation,
      typingSpeed,
      scrollBehavior,
    } = interactionData;

    const interactionComplexity =
      avgCursorSpeed * 0.3 +
      cursorAcceleration * 0.2 +
      pathDeviation * 0.2 +
      typingSpeed * 0.2 +
      scrollBehavior * 0.1;

    return parseFloat(interactionComplexity.toFixed(2));
  };

  // Function to calculate typing accuracy
  const calculateTypingAccuracy = () => {
    const totalKeystrokes = keystrokeDurations.length;
    const correctKeystrokes = totalKeystrokes - errorCount;
    const typingAccuracy =
      totalKeystrokes > 0
        ? ((correctKeystrokes / totalKeystrokes) * 100).toFixed(2)
        : 100;

    return parseFloat(typingAccuracy);
  };

  // Function to parse and sanitize data before sending
  const sanitizeData = () => {
    const {
      avgCursorSpeed,
      cursorAcceleration,
      pathDeviation,
      idleTime,
      jitter,
      clickPattern,
      typingSpeed,
      keyPressDuration,
      keyTransitionTime,
      keyTransitionStdDev,
      typingAccuracy,
      errorRate,
      sessionDuration,
      averageDwellTime,
      scrollBehavior,
      interactionComplexity,
    } = interactionData;

    // Validate numerical fields
    const isValid =
      avgCursorSpeed >= 0 &&
      Math.abs(cursorAcceleration) <= MAX_HUMAN_ACCELERATION &&
      pathDeviation >= 0 &&
      idleTime >= 0 &&
      jitter >= 0 &&
      clickPattern >= 0 &&
      typingSpeed >= 0 &&
      keyPressDuration >= 0 &&
      keyTransitionTime >= 0 &&
      keyTransitionStdDev >= 0 &&
      typingAccuracy >= 0 &&
      errorRate >= 0 &&
      sessionDuration >= 0 &&
      averageDwellTime >= 0 &&
      scrollBehavior >= 0 &&
      interactionComplexity >= 0;

    return isValid;
  };

  // Login handler with data submission
  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent form submission

    console.log('handleLogin triggered');

    // Capture the end time of the session
    const endTime = performance.now();
    const sessionDurationInSeconds =
      (endTime - sessionStartRef.current) / 1000; // Convert milliseconds to seconds

    // Compute averages and derived metrics
    const totalKeyPressDuration = keystrokeDurations.reduce(
      (sum, dur) => sum + dur,
      0
    );
    const averageKeyPressDuration =
      keystrokeDurations.length > 0
        ? totalKeyPressDuration / keystrokeDurations.length
        : 0;

    const typingSpeed =
      sessionDurationInSeconds > 0
        ? (
            keystrokeDurations.length /
            (sessionDurationInSeconds / 60)
          ).toFixed(2)
        : 0; // chars per minute

    const averageKeyTransitionTime =
      keyTransitionTimes.length > 0
        ? (
            keyTransitionTimes.reduce((sum, time) => sum + time, 0) /
            keyTransitionTimes.length
          ).toFixed(2)
        : 0;

    const keyTransitionStdDev = calculateStandardDeviation(
      keyTransitionTimes
    ).toFixed(2);

    const averageDwellTime =
      dwellTimes.length > 0
        ? (
            dwellTimes.reduce((sum, time) => sum + time, 0) /
            dwellTimes.length
          ).toFixed(2)
        : 0;

    const errorRate =
      keystrokeDurations.length > 0
        ? ((errorCount / keystrokeDurations.length) * 100).toFixed(2)
        : 0;

    const typingAccuracy = calculateTypingAccuracy();

    const interactionComplexity = calculateInteractionComplexity();

    const formattedData = {
      userId:
        aadhaarNumber.trim() !== '' ? aadhaarNumber.trim() : 'anonymous-user',
      avgCursorSpeed: parseFloat(interactionData.avgCursorSpeed.toFixed(2)),
      cursorAcceleration: parseFloat(
        interactionData.cursorAcceleration.toFixed(2)
      ),
      pathDeviation: parseFloat(interactionData.pathDeviation.toFixed(2)),
      idleTime: parseFloat(idleTime.toFixed(2)),
      jitter: parseFloat(interactionData.jitter.toFixed(2)),
      clickPattern: clickCount,
      typingSpeed: parseFloat(typingSpeed),
      keyPressDuration: parseFloat(averageKeyPressDuration.toFixed(2)),
      keyTransitionTime: parseFloat(averageKeyTransitionTime),
      keyTransitionStdDev: parseFloat(keyTransitionStdDev),
      typingAccuracy: typingAccuracy,
      errorRate: parseFloat(errorRate),
      sessionDuration: parseFloat(sessionDurationInSeconds.toFixed(2)),
      averageDwellTime: parseFloat(averageDwellTime),
      scrollBehavior: parseFloat(scrollDistance.toFixed(2)),
      interactionComplexity: interactionComplexity,
    };

    // Debugging: Log formatted data before sending
    console.log('Formatted Data:', formattedData);

    // Validate data before sending
    if (!sanitizeData()) {
      setErrorMessage('Detected invalid interaction data. Please try again.');
      console.log('Data validation failed.');
      return;
    }

    try {
      const response = await axios.post(
        'http://192.168.1.10:5000/api/collect-data', // Replace with your server's local IP
        formattedData
      );
      console.log('Server Response:', response.data);

      if (response.data.prediction === 'Human') {
        alert('Login successful!');
        // Export data to CSV
        exportToCSV(formattedData);
        console.log('CSV export triggered.');
        // Proceed with granting access or redirecting
      } else {
        setErrorMessage('Verification failed. Please try again.');
        console.log('Verification failed.');
        // Optionally, reset interaction data and form
      }
    } catch (error) {
      console.error('Error sending interaction data to backend:', error);
      setErrorMessage('Failed to send data to the server. Please try again.');
    }

    // Reset session start time for the next session
    sessionStartRef.current = performance.now();
  };

  // Handler for the "Confirm you're human" checkbox
  const handleChange = () => {
    setIsChecked(!isChecked);
  };

  // Slides for the carousel
  const slides = [
    {
      id: 1,
      image: flagImage,
      alt: 'Se',
      title: 'Pride of India',
      description:
        'Aadhar verification ensures every citizen is part of the digital revolution, empowering India for a brighter future.',
    },
    {
      id: 2,
      image: student,
      alt: 'Easy Verification',
      title: 'Enabling Education',
      description:
        'Aadhar unlocks access to scholarships and academic opportunities, shaping the future of young India.',
    },
    {
      id: 3,
      image: farmer,
      alt: 'Quick Access',
      title: 'Empowering Farmers',
      description:
        'Access government schemes and subsidies seamlessly with Aadhar-based services tailored for the backbone of our nation.',
    },
    {
      id: 4,
      image: working,
      alt: 'Quick Access',
      title: 'Supporting the Workforce',
      description:
        'Aadhar helps workers secure their rights, enabling access to essential services and opportunities for growth.',
    },
  ];

  // Animation Effects
  useEffect(() => {
    // Form animation: fade from left to right
    gsap.fromTo(
      formRef.current,
      { x: '-100%', opacity: 0 },
      { x: '0%', opacity: 1, duration: 1.5, ease: 'power3.out' }
    );

    // Carousel animation: fade from right to left
    gsap.fromTo(
      carouselRef.current,
      { x: '100%', opacity: 0 },
      { x: '0%', opacity: 1, duration: 1.5, ease: 'power3.out' }
    );

    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="flex flex-col md:flex-row-reverse min-h-screen bg-gray-100 overflow-hidden">
      {/* Login Form Column (Now on the Right) */}
      <div
        className="w-full md:w-1/2 flex items-center justify-center p-8"
        ref={formRef}
      >
        <div className="w-full max-w-md space-y-8">
          <div className="text-center">
            <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
              Verify Your Identity
            </h2>
            <p className="mt-2 text-sm text-gray-600">
              Please enter the required details to verify your email or mobile
              number
            </p>
          </div>
          <form className="mt-8 space-y-6" onSubmit={handleLogin}>
            {/* Aadhaar Number Input */}
            <div className="form-control w-full">
              <label htmlFor="aadhar-number" className="label">
                <span className="label-text text-lg font-bold text-black">
                  Aadhaar Number
                </span>
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <User
                    className="h-5 w-5 text-gray-400"
                    aria-hidden="true"
                  />
                </div>
                <input
                  id="aadhar-number"
                  name="aadhar"
                  type="text"
                  required
                  placeholder="Enter 12 Digit Aadhaar Number"
                  className="input input-bordered w-full pl-10 bg-white"
                  onChange={handleAadhaarChange}
                  value={aadhaarNumber}
                />
              </div>
            </div>

            {/* Confirm you're human Checkbox */}
            <label className="inline-flex items-center cursor-pointer ml-20">
              <div className="relative">
                <input
                  type="checkbox"
                  className="sr-only"
                  checked={isChecked}
                  onChange={handleChange}
                  required // Make it required
                />
                <div
                  className={`w-6 h-6 border-2 rounded-md ${
                    isChecked
                      ? 'border-green-500 bg-green-50'
                      : 'border-gray-500 bg-white'
                  } transition-colors duration-200 ease-in-out`}
                >
                  <motion.svg
                    className="w-full h-full text-green-500"
                    fill="none"
                    viewBox="0 0 24 24"
                    initial="hidden"
                    animate={isChecked ? 'visible' : 'hidden'}
                    variants={{
                      visible: {
                        pathLength: 1,
                        opacity: 1,
                        transition: { duration: 0.2, ease: 'easeInOut' },
                      },
                      hidden: {
                        pathLength: 0,
                        opacity: 0,
                        transition: { duration: 0.2, ease: 'easeInOut' },
                      },
                    }}
                  >
                    <motion.path
                      d="M5 13l4 4L19 7"
                      stroke="currentColor"
                      strokeWidth={3}
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </motion.svg>
                </div>
              </div>
              <span className="ml-3 text-lg font-medium text-gray-700">
                Confirm you're human
              </span>
            </label>

            {/* Error Message */}
            {errorMessage && (
              <p className="text-red-500 text-center">{errorMessage}</p>
            )}

            {/* Login Button */}
            <div>
              <button
                type="submit"
                className="btn bg-[#046A38] text-white w-full flex justify-center hover:bg-[#FF671F] border-none focus:ring-0 relative"
                disabled={aadhaarNumber.length !== 12}
              >
                <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                  <Lock
                    className="h-5 w-5 text-white text-xl"
                    aria-hidden="true"
                  />
                </span>
                Login with OTP
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Auto Carousel Column (Now on the Left) */}
      <div
        className="w-full md:w-1/2 bg-indigo-600 relative overflow-hidden"
        ref={carouselRef}
      >
        <div className="absolute inset-0 flex items-center justify-center">
          {slides.map((slide, index) => (
            <div
              key={slide.id}
              className={`absolute inset-0 flex items-center justify-center transition-opacity duration-1000 ease-in-out ${
                index === currentSlide ? 'opacity-100' : 'opacity-0'
              }`}
            >
              <img
                src={slide.image}
                alt={slide.alt}
                className="absolute inset-0 object-cover w-full h-full"
              />
              <div className="absolute inset-0 bg-black opacity-50"></div>
              <div className="absolute text-white text-center px-4">
                <h3 className="text-4xl font-bold mb-4">{slide.title}</h3>
                <p className="text-xl">{slide.description}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Carousel Indicators */}
        <div className="absolute bottom-5 left-0 right-0 flex justify-center space-x-2 ">
          {slides.map((_, index) => (
            <button
              key={index}
              className={`w-3 h-3 rounded-full ${
                index === currentSlide
                  ? 'bg-white'
                  : 'bg-white bg-opacity-50'
              }`}
              onClick={() => setCurrentSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
            ></button>
          ))}
        </div>

        {/* Previous Slide Button */}
        <button
          className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-white bg-opacity-30 rounded-full p-2 focus:outline-none hover:bg-opacity-50 transition duration-150 ease-in-out mt-20"
          onClick={() =>
            setCurrentSlide(
              (prev) => (prev - 1 + slides.length) % slides.length
            )
          }
          aria-label="Previous slide"
        >
          <ChevronLeft className="h-6 w-6 text-white" />
        </button>

        {/* Next Slide Button */}
        <button
          className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-white bg-opacity-30 rounded-full p-2 focus:outline-none hover:bg-opacity-50 transition duration-150 ease-in-out mt-20"
          onClick={() => setCurrentSlide((prev) => (prev + 1) % slides.length)}
          aria-label="Next slide"
        >
          <ChevronRight className="h-6 w-6 text-white" />
        </button>
      </div>
    </div>
  );
};

export default CombinedLogin;
